{"settings":"{
  "editor.quickSuggestions": {
    "strings": true
  },
  "element-helper.version": "2.4",
"emmet.triggerExpansionOnTab": true,
"git.path": "C:\\Program Files\\Git\\bin\\git.exe",
  "editor.wordWrap": "bounded",
  // "workbench.startupEditor": "newUntitledFile",
  "vsicons.dontShowNewVersionMessage": true,
  "window.zoomLevel": 0,
  "workbench.startupEditor": "newUntitledFile",
  "gitlens.advanced.messages": {
    "suppressShowKeyBindingsNotice": true,
    "suppressWelcomeNotice": true
  },
  "[vue]": {
    "editor.defaultFormatter": "octref.vetur"
  },
  // Whether to have initial indent for <script> region
  "vetur.format.scriptInitialIndent": true,
  // Whether to have initial indent for <style> region
  "vetur.format.styleInitialIndent": true,
  "vetur.format.defaultFormatter.html": "prettyhtml",
  "vetur.format.defaultFormatterOptions": {
    "prettyhtml": {
      "printWidth": 1000, // No line exceeds 100 characters
      "singleQuote": false, // Prefer double quotes over single quotes
      "wrapAttributes": false,
    }
  },



  //  "powermode.enabled": true,
  // "phpfmt.php_bin": "D:/software/xampp/php/php.exe",
  // 是否启用自动拉取
  "git.autofetch": true,
  "editor.minimap.enabled": true,
  "liveServer.settings.donotShowInfoMsg": true,
  "editor.detectIndentation": false,
  "editor.tabSize": 2,
  "files.associations": {
    "*.cjson": "jsonc",
    "*.wxss": "css",
    "*.wxs": "javascript",
    ".env": "javascript"
  },
  "emmet.includeLanguages": {
    "wxml": "html"
  },
  "minapp-vscode.disableAutoConfig": true,
  "npm.enableScriptExplorer": true,
  "editor.wordWrapColumn": 1000,
  "javascript.updateImportsOnFileMove.enabled": "always",
  "typescript.updateImportsOnFileMove.enabled": "always",
  "[javascript]": {
    "editor.defaultFormatter": "HookyQR.beautify"
  },
  "gitlens.views.repositories.location": "explorer",
  "editor.fontFamily": "'Fira Code','Cascadia Code','Fira Mono', Consolas, 'Courier New', monospace",
  "editor.fontLigatures": true,
  "[javascriptreact]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[less]": {
    "editor.defaultFormatter": "HookyQR.beautify"
  },
  "[html]": {
    "editor.defaultFormatter": "vscode.html-language-features"
  },
  "[typescript]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "workbench.iconTheme": "vscode-icons",
  "editor.renderWhitespace": "all",
  "editor.renderControlCharacters": true,
  "[typescriptreact]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "git.ignoreLegacyWarning": true,
  "[json]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "breadcrumbs.filePath": "last",
  "[css]": {
    "editor.defaultFormatter": "HookyQR.beautify"
  },
  "terminal.integrated.shell.windows": "C:\\Program Files\\PowerShell\\7\\pwsh.exe",
  "workbench.tree.indent": 4,
  "telemetry.enableCrashReporter": false,
  "diffEditor.ignoreTrimWhitespace": false,

}"}
